import React, { useState } from 'react'
import Task from './task'
import {v4 as uuidy4} from 'uuid';
import './stile.css';
export default function Form_task() {
    const [tasks,settasks]=useState([

        {id:uuidy4(),nom:'REACT'},
        {id:uuidy4(),nom:'FIGMA'}
    ])
    const [task,setTask]=useState('');
    const addTask=()=>{
      let ntasks=[...tasks];
      let ntask={};

     ntask.id=uuidy4();

     ntask.nom=task;

     ntasks.push(ntask);
     settasks(ntasks);
     setTask('');

    }
    const supp=(idp)=>{
        alert('Etes-vous s청r de vouloir supprimer cette t창che'+idp+' ?');

        let ntasks=tasks.filter((t)=>{
          return t.id!=idp
        })
        settasks(ntasks);
      }
  return (

    <div className=''>

      <h1>To do list </h1>
      <form >
 <input type='text' id="n1" placeholder='enter a new task ' value={task} onChange={(e)=>{setTask(e.target.value)}}/><input id="n" type='button' onClick={addTask} value="Add" />

      </form>
      <br/><br/>
      {
            tasks.map((t)=>{
                return <p key={t.id}><Task delf={()=>supp(t.id)} txt={t.nom}/> </p> 
            })
        }




    </div>
  ) 
}
